import React, {useState} from 'react'; //Enables JSX
import ReactDOM from 'react-dom/client' //Allows us to Attach the App
const root = ReactDOM.createRoot(document.getElementById('app'))


 const textLinks = [
    {id: 1, home: "HOME"},
    {id: 2, about: "ABOUT"},
    {id: 3, something: "IDFK"},
 ]


 const ButtonsClick = (props) => {
     props.props.map(eachButton => {
        return (
            <>
            <a href="">{eachButton.home}</a>,
            <a href="">{eachButton.about}</a>,
            <a href="">{eachButton.something}</a>
            </>
        )
    })
}



const Header = () => {

    return (
    <header>
        <h1>CHON ALBUMS</h1>
        <img className="chonLogo" src="https://d1h6olfj08dopz.cloudfront.net/images/41392/Chon_Logo_NavBar_Grow.png" alt=""/>
        <nav className="wholenav">

        <ButtonsClick props={textLinks}/>
        </nav>
    </header>
    )
}




//compenet is a function that returns HTML template
const Main = () => {
    return (
        <main>
            <Header thoseLinks={textLinks}/>
        </main>
         )
}


root.render(<Main />);
